import React, { useEffect, useState } from "react";
import { getAllHippos } from "../controllers/getAllHippos";
import { addHippo } from "../controllers/addHippo";
import { getTestResults } from "../controllers/getTestResults";
import { addTestResult } from "../controllers/addTestResult";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { Input } from "@material-ui/core";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import HippoInfo from "./HippoInfo";
import "./index.scss";

const HippoCentral = () => {
  const [hippos, setHippos] = useState([]);
  const [hippoName, setHippoName] = useState("");
  const [testResults, setTestResults] = useState([]);

  const getHippos = () => {
    getAllHippos()
      .then((res) => {
        setHippos(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getHippoTestResults = () => {
    getTestResults()
      .then((res) => {
        setTestResults(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const renderTestResult = (hippo) => {
    let output;
    let report = 0;
    testResults.forEach((result) => {
      if (result.patient_id === hippo.id) {
        output = result?.resource?.valueCodeableConcept?.text;
      }
    });

    if (output) {
      return output;
    } else {
      return (
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={report}
          onChange={(e) => {
            report = e.target.value;
            console.log(e.target.value);
            // addTestResult({
            //   resourceType: "Observation",
            //   code: "",
            //   subject: { reference: `Patient/${hippo.id}` },
            // });
          }}
        >
          <MenuItem value={0}></MenuItem>
          <MenuItem value={1}>Positive for Hippo Virus</MenuItem>
          <MenuItem value={2}>Negative for Hippo Virus</MenuItem>
          <MenuItem value={3}>Inconclusive result for Hippo Virus</MenuItem>
        </Select>
      );
    }
  };

  useEffect(() => {
    getHippos();
    getHippoTestResults();
  }, []);

  return (
    <div>
      <h1>Hippo Central</h1>
      <Input
        value={hippoName}
        type="text"
        placeholder="hippo's name"
        onChange={(e) => {
          setHippoName(e.target.value);
        }}
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            addHippo(hippoName, () => {
              getHippos();
              setHippoName("");
            });
          }
        }}
      ></Input>

      <TableContainer component={Paper}>
        <Table className="table" aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Hippo Name</TableCell>
              <TableCell align="right">Test Result</TableCell>
              <TableCell align="right">Updated At</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {hippos.map((hippo) => (
              <HippoInfo hippo={hippo} />
              // <TableRow key={hippo.id}>
              //   <TableCell component="th" scope="row">
              //     {hippo?.resource?.name?.map((n) => {
              //       return n.given;
              //     })}
              //   </TableCell>
              //   <TableCell align="right">{renderTestResult(hippo)}</TableCell>
              //   <TableCell align="right">
              //     {new Date(hippo.updated_at).toISOString().split("T")[0]}
              //   </TableCell>
              // </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default HippoCentral;
