import React, { useEffect, useState } from "react";
import HippoInfo from "./HippoInfo";
import { getAllHippos } from "../controllers/getAllHippos";
import { addHippo } from "../controllers/addHippo";
import "./index.scss";

const HippoCentral = () => {
  const [hippos, setHippos] = useState([]);
  const [hippoName, setHippoName] = useState("");

  useEffect(() => {
    getAllHippos()
      .then((res) => {
        setHippos(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  console.log(hippos);
  return (
    <div>
      <h1>Hippo Central</h1>
      <input
        type="text"
        placeholder="hippo's name"
        onChange={(e) => {
          setHippoName(e.target.value);
        }}
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            console.log("do validate");
          }
        }}
      ></input>
      {hippos.map((hippo) => {
        return (
          <div key={hippo.id} className="hippo-central">
            <HippoInfo hippo={hippo} />
          </div>
        );
      })}
    </div>
  );
};

export default HippoCentral;
