import React, { useEffect, useState } from "react";
import { getAllHippos } from "../controllers/getAllHippos";
import { addHippo } from "../controllers/addHippo";
import { getTestResults } from "../controllers/getTestResults";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import "./index.scss";

const HippoCentral = () => {
  const [hippos, setHippos] = useState([]);
  const [hippoName, setHippoName] = useState("");
  const [testResults, setTestResults] = useState([]);

  const getHippos = () => {
    getAllHippos()
      .then((res) => {
        setHippos(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getHippoTestResults = () => {
    getTestResults()
      .then((res) => {
        setTestResults(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const renderTestResult = (hippo) => {
    testResults.forEach((result) => {
      if (result.patient_id === hippo.id) {
        console.log(hippo);
      }
    });
  };

  useEffect(() => {
    getHippos();
    getHippoTestResults();
  }, []);

  return (
    <div>
      <h1>Hippo Central</h1>
      <input
        value={hippoName}
        type="text"
        placeholder="hippo's name"
        onChange={(e) => {
          setHippoName(e.target.value);
        }}
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            addHippo(hippoName, () => {
              getHippos();
              setHippoName("");
            });
          }
        }}
      ></input>

      <TableContainer component={Paper}>
        <Table className="table" aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Hippo Name</TableCell>
              <TableCell align="right">Test Result</TableCell>
              <TableCell align="right">Updated At</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {hippos.map((hippo) => (
              <TableRow key={hippo.id}>
                <TableCell component="th" scope="row">
                  {hippo?.resource?.name?.map((n) => {
                    return n.given;
                  })}
                </TableCell>
                <TableCell align="right">{renderTestResult(hippo)}</TableCell>
                <TableCell align="right">
                  {new Date(hippo.updated_at).toISOString().split("T")[0]}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default HippoCentral;
