import React, { useState } from "react";
import TableCell from "@material-ui/core/TableCell";
import TableRow from "@material-ui/core/TableRow";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";

const HippoInfo = ({ hippo, testResults }) => {
  const [result, setResult] = useState("");

  const renderTestResult = (hippo) => {
    let output;
    testResults.forEach((result) => {
      if (result.patient_id === hippo.id) {
        const referenceResult = result?.resource?.subject?.reference;
        const textResult = result?.resource?.valueCodeableConcept?.text;
        console.log(result);
        output = result?.resource?.valueCodeableConcept?.text;
      }
    });

    if (output) {
      return output;
    } else {
      return (
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={result}
          onChange={(e) => {
            setResult(e.target.value);
            // addTestResult({
            //   resourceType: "Observation",
            //   code: "",
            //   subject: { reference: `Patient/${hippo.id}` },
            // });
          }}
        >
          <MenuItem value={0}></MenuItem>
          <MenuItem value={1}>Positive for Hippo Virus</MenuItem>
          <MenuItem value={2}>Negative for Hippo Virus</MenuItem>
          <MenuItem value={3}>Inconclusive result for Hippo Virus</MenuItem>
        </Select>
      );
    }
  };

  return (
    <TableRow key={hippo.id}>
      <TableCell component="th" scope="row">
        {hippo?.resource?.name?.map((n) => {
          return n.given;
        })}
      </TableCell>
      <TableCell align="right">{renderTestResult(hippo)}</TableCell>
      <TableCell align="right">
        {new Date(hippo.updated_at).toISOString().split("T")[0]}
      </TableCell>
    </TableRow>
  );
};

export default HippoInfo;
