import React, { useEffect, useState } from "react";
import HippoInfo from "./HippoInfo";
import { getAllHippos } from "../controllers/getAllHippos";
import { addHippo } from "../controllers/addHippo";
import "./index.scss";

const HippoCentral = () => {
  const [hippos, setHippos] = useState([]);

  useEffect(() => {
    getAllHippos()
      .then((res) => {
        setHippos(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
      <h1>Hippo Central</h1>
      <button
        onClick={() => {
          addHippo();
          console.log(addHippo);
        }}
      >
        ADD HIPPO
      </button>
      {hippos.map((hippo) => {
        return (
          <div key={hippo.id} className="hippo-central">
            <HippoInfo hippo={hippo} />
          </div>
        );
      })}
    </div>
  );
};

export default HippoCentral;
